import React, { useContext } from 'react'
import { useState } from 'react'
import axios from 'axios'
function UploadPostContent() {
  const userId = JSON.parse(localStorage.getItem('LogInUser'))._id
  const [postData, setpostData] = useState({
    title: '',
    category: '',
    image: '',
    userId: userId,
  })
  const handle = (e) => {
    setpostData({ ...postData, [e.target.name]: e.target.value })
  }
  const submit = (e) => {
    e.preventDefault()
    const getFullName = () => {
      console.log('full name api calall')
      axios({
        method: 'post',
        url: 'http://localhost:5000/getFullName',
        data: { userId: JSON.parse(localStorage.getItem('LogInUser'))._id },
      }).then((result) => {
        console.log('sdsds', result)
      })
    }

    let formdata = new FormData(e.target)
    formdata.append('userId', JSON.parse(localStorage.getItem('LogInUser'))._id)
    formdata.append('fullName', getFullName())
    console.log('idddd', formdata)
    axios({
      method: 'POST',
      data: formdata,
      withCredentials: true,
      url: 'http://localhost:5000/UploadPostContent',
      config: { headers: { 'Content-Type': 'multipart/form-data' } },
    }).then((res) => {
      if (res.data.status) {
        console.log('added')
      } else {
        console.log('not aded')
      }
    })
  }

  return (
    <>
      <form onSubmit={submit}>
        <lable>Title</lable>
        <input type="text" name="title" onChange={handle} />
        <lable>Category</lable>
        <select name="category" onChange={handle}>
          <option value="Cat" selected>
            Cat
          </option>
          <option value="Dog">Dog</option>
          <option value="Rabbit">Rabbit</option>
          <option value="Bird">Bird</option>
          <option value="Other">Other</option>
        </select>
        <lable>Chose Photos</lable>
        <input type="file" name="image" onChange={handle} />
        <input type="submit" />
      </form>
    </>
  )
}
export default UploadPostContent
